package examples

import (
	"context"
	"os"

	"github.com/hetznercloud/hcloud-go/v2/hcloud"
)

import (
	"net"
)

func main() {
	token := os.Getenv("HCLOUD_TOKEN")

	client := hcloud.NewClient(hcloud.WithToken(token))
	ctx := context.TODO()

	result, _, err := client.Firewall.Create(ctx, hcloud.FirewallCreateOpts{
		ApplyTo: []hcloud.FirewallResource{
			{
				Type: hcloud.FirewallResourceTypeServer,
				Server: &hcloud.FirewallResourceServer{
					ID: 42,
				},
			},
		},
		Labels: map[string]string{
			"env": "dev",
		},
		Name: "Corporate Intranet Protection",
		Rules: []hcloud.FirewallRule{
			{
				Description: hcloud.Ptr("Allow port 80"),
				Direction:   hcloud.FirewallRuleDirectionIn,
				Port:        hcloud.Ptr("80"),
				Protocol:    hcloud.FirewallRuleProtocolTCP,
				SourceIPs: []net.IPNet{
					{
						IP:   net.ParseIP("28.239.13.1"),
						Mask: net.CIDRMask(32, 32),
					},
					{
						IP:   net.ParseIP("28.239.14.0"),
						Mask: net.CIDRMask(24, 32),
					},
					{
						IP:   net.ParseIP("ff21:1eac:9a3b:ee58:5ca:990c:8bc9:c03b"),
						Mask: net.CIDRMask(128, 128),
					},
				},
			},
		},
	})

	err = client.Action.WaitFor(ctx, result.Actions...)

	firewall := result.Firewall
}

