package examples

import (
	"context"
	"os"
	"time"

	"github.com/hetznercloud/hcloud-go/v2/hcloud"
)

func main() {
	token := os.Getenv("HCLOUD_TOKEN")

	client := hcloud.NewClient(hcloud.WithToken(token))
	ctx := context.TODO()

	result, _, err := client.LoadBalancer.Create(ctx, hcloud.LoadBalancerCreateOpts{
		Algorithm: &hcloud.LoadBalancerAlgorithm{
			Type: hcloud.LoadBalancerAlgorithmTypeRoundRobin,
		},
		Labels: map[string]string{
			"environment":    "prod",
			"example.com/my": "label",
			"just-a-key":     "",
		},
		LoadBalancerType: &hcloud.LoadBalancerType{Name: "lb11"},
		Location:         &hcloud.Location{Name: "fsn1"},
		Name:             "Web Frontend",
		Network:          &hcloud.Network{ID: 123},
		NetworkZone:      hcloud.NetworkZoneEUCentral,
		PublicInterface:  hcloud.Ptr(true),
		Services: []hcloud.LoadBalancerCreateOptsService{
			{
				DestinationPort: hcloud.Ptr(80),
				HealthCheck: &hcloud.LoadBalancerCreateOptsServiceHealthCheck{
					HTTP: &hcloud.LoadBalancerCreateOptsServiceHealthCheckHTTP{
						Domain:      hcloud.Ptr("example.com"),
						Path:        hcloud.Ptr("/"),
						Response:    hcloud.Ptr(`{"status": "ok"}`),
						StatusCodes: []string{"2??", "3??"},
						TLS:         hcloud.Ptr(false),
					},
					Interval: hcloud.Ptr(15 * time.Second),
					Port:     hcloud.Ptr(4711),
					Protocol: hcloud.LoadBalancerServiceProtocolHTTP,
					Retries:  hcloud.Ptr(3),
					Timeout:  hcloud.Ptr(10 * time.Second),
				},
				HTTP: &hcloud.LoadBalancerCreateOptsServiceHTTP{
					Certificates:   []*hcloud.Certificate{{ID: 897}},
					CookieLifetime: hcloud.Ptr(300 * time.Second),
					CookieName:     hcloud.Ptr("HCLBSTICKY"),
					RedirectHTTP:   hcloud.Ptr(true),
					StickySessions: hcloud.Ptr(true),
				},
				ListenPort:    hcloud.Ptr(443),
				Protocol:      hcloud.LoadBalancerServiceProtocolHTTPS,
